  var acc = document.getElementsByClassName("accordion");
  var panel = document.getElementsByClassName("panel");
  var i;

  for (i = 0; i < acc.length; i++) {
    acc[i].addEventListener("click", function() {
      var isActive = this.classList.contains("active");
      closeAllPanels();
      if (!isActive) {
        this.classList.toggle("active");
        var currentPanel = this.nextElementSibling;
        currentPanel.style.display = "block";
      }
    });
  }

  function closeAllPanels() {
    for (i = 0; i < panel.length; i++) {
      panel[i].style.display = "none";
      acc[i].classList.remove("active");
    }
  }

  var sections = document.getElementsByClassName("section");

  // Fechar os painéis quando outra seção é aberta
  for (i = 0; i < sections.length; i++) {
    sections[i].addEventListener("click", function(event) {
      var isOpened = this.querySelector(".accordion").classList.contains("active");
      if (!isOpened) {
        closeAllPanels();
      }
    });
  }

  // Fechar os painéis quando ocorrer um clique fora da seção "five"
  document.addEventListener("click", function(event) {
    if (!event.target.closest("#five")) {
      closeAllPanels();
    }
  });



